mapper.select:
select  from app_menu

     and menu_type = #{menuType} 
     and title like concat('%', #{title}, '%') 
     and type = #{type} 
     and status = #{status} 
     and parent_id = #{parentId} 

order by sort asc, id desc

mapper.update:
update app_menu
set menu_type = #{menuType}, title = #{title}, icon = #{icon}, type = #{type}, path = #{path}, sort = #{sort}, status = #{status}, parent_id = #{parentId}, remark = #{remark}
where id = #{id}
