mapper.select:


    and user_id = #{userId}
    and brand_id = #{brandId}
    and mining_user_name like concat('%', #{miningUserName}, '%')
    and api_code like concat('%', #{apiCode}, '%')
    and status = #{status}

order by id desc

mapper.update:
update app_user_miner
user_id = #{userId}, brand_id = #{brandId}, mining_user_name = #{miningUserName}, api_code = #{apiCode}, management_fee_rate = #{managementFeeRate}, estimated_today_income = #{estimatedTodayIncome}, total_income = #{totalIncome}, yesterday_income = #{yesterdayIncome}, status = #{status},
where id = #{id}
